"use client";

import {
  HomeIcon,
  ClipboardDocumentCheckIcon,
  ChartBarIcon,
  EnvelopeIcon,
  BellAlertIcon,
  ArrowTrendingUpIcon
} from "@heroicons/react/24/outline";
import Link from "next/link";
import { usePathname } from "next/navigation";
import clsx from "clsx";

// Map of links to display in the side navigation.
// Depending on the size of the application, this would be stored in a database.
const links = [
  { name: "Home", href: "/dashboard", icon: Home },
  // {
  //   name: "Invoices",
  //   href: "/dashboard/invoices",
  //   icon: DocumentDuplicateIcon
  // },
  // { name: "Customers", href: "/dashboard/customers", icon: UserGroupIcon },
  { name: "Status", href: "/dashboard/status", icon: Status },
  { name: "Reminders", href: "/dashboard/reminders", icon: Notification },
  { name: "Reports", href: "/dashboard/reports", icon: Reports }
  // { name: "Graphs and Charts", href: "#", icon: Charts },
  // { name: "Recipe Analyzer", href: "#", icon: MealIcon }
];

export default function NavLinks() {
  const pathname = usePathname();
  return (
    <>
      {links.map((link) => {
        const LinkIcon = link.icon;
        return (
          <Link
            key={link.name}
            href={link.href}
            className={clsx(
              "flex h-[48px] grow items-center justify-center gap-2 rounded-md bg-gray-50 p-3 text-sm font-medium hover:bg-sky-100 hover:text-blue-600 md:flex-none md:justify-start md:p-2 md:px-3",
              {
                "bg-sky-100 text-blue-600": pathname === link.href
              }
            )}
          >
            <LinkIcon />
            <p className="hidden md:block">{link.name}</p>
          </Link>
        );
      })}
    </>
  );
}

